<template>
  <component :is="tag" class="qs-translate" v-if="data">{{ data[value] || value }}</component>
  <component :is="tag" class="qs-translate" v-else v-code="{code,value,module}"></component>
</template>

<script>
export default {
  // 表码翻译组件
  name: 'qs-translate',
  props: {
    tag: {
      type: String,
      default: 'span'
    },
    code: {
      type: String,
      required: true
    },
    module: String,
    // 使用 data 或者 v-code 进行翻译
    data: Object,
    value: String
  },
  data() {
    return {
      text: ''
    };
  },
  methods: {}
};
</script>

<style lang="scss" scoped></style>
