<template>
  <el-dropdown trigger="click" @visible-change="handleCommand" class="setting">
    <div class="u-skins__base" :style="{ height: height }">
      <span class="settingIcon" :class="isSetting ? 'hoverSetting' : ''" :style="{ padding: padding }">
        <i class="icon  iconl-setting el-dropdown-link" :style="{ fontSize: fontSize }"></i>
      </span>
    </div>
    <el-dropdown-menu slot="dropdown">
      <el-dropdown-item @click.native="openFun('/bs-solution')" v-permission="['BSFAPZ']"
        >背审方案配置</el-dropdown-item
      >
      <el-dropdown-item @click.native="handleClickJump()">常见问题</el-dropdown-item>
      <el-dropdown-item @click.native="openFun('/about-us')">关于我们</el-dropdown-item>
    </el-dropdown-menu>
  </el-dropdown>
</template>

<script>
export default {
  name: 'setting',
  props: {
    height: {
      type: String,
      default: '33px'
    },
    fontSize: {
      type: String,
      default: '20px'
    },
    padding: {
      type: String,
      default: '4px'
    }
  },
  data() {
    return {
      isSetting: false
    };
  },
  computed: {},
  methods: {
    /* 设置*/
    handleCommand(command) {
      if (command) {
        this.isSetting = true;
      } else {
        this.isSetting = false;
      }
    },

    openFun(type) {
      if (type) {
        this.$router.push(type);
      }
    },
    /**
     * @MethodName:
     * @Description: 跳转
     * @Param:
     * @Return:
     * @Author: lkh
     * @Date: 2019/12/13 15:39
     * */
    handleClickJump() {
      this.$router.push('/system-msg');
    }
  },
  mounted() {}
};
</script>

<style scoped lang="scss"></style>
