<template>
  <div class="tab-list">
    <ul class="tab__items clearfix">
      <li
        class="tab__item"
        @click="changeTab(item)"
        v-for="(item, index) in tabItemsData"
        :class="{ 'is-active': index == curTabType }"
        :key="index"
      >
        {{ item.name }}
        <span v-if="isShow"
          >(<span class="is-danger" style="margin: 0 2px">{{ item.number }}</span
          >)</span
        >
      </li>
    </ul>
    <slot name="option"></slot>
  </div>
</template>

<script>
export default {
  name: 'tabList',
  data() {
    return {};
  },
  props: {
    tabItemsData: {
      type: Array,
      default: () => []
    },
    curTabType: {
      type: Number | String,
      default: 0
    },
    isShow: {
      type: Boolean,
      default: false
    }
  },
  methods: {
    changeTab(obj) {
      this.$emit('changeData', obj);
    }
  }
};
</script>

