<template>
  <div class="bs-home-total__box">
    <div v-for="(item, index) in data" :key="index" class="bs-home-total">
      <span :class="['is-name', item.type == 1 ? 'is-yellow' : item.type == 2 ? 'is-pink' : '']">{{ item.name }} </span>
      <span class="is-num" v-html="item.num"></span>
    </div>
  </div>
</template>

<script>
export default {
  name: 'totalExhibition',
  props: {
    bszs: { type: Number | String, default: 0 },
    qkrs: { type: Number | String, default: 0 },
    wxsjrs: { type: Number | String, default: 0 }
  },
  watch: {
    bszs(val) {
      this.data[0].num = val;
    },
    qkrs(val) {
      this.data[1].num = val;
    },
    wxsjrs(val) {
      this.data[2].num = val;
    }
  },
  data() {
    return {
      data: [
        {
          type: 0,
          name: '背审总数',
          num: ''
        },
        {
          type: 1,
          name: '可疑人数',
          num: ''
        },
        {
          type: 2,
          name: '无效人数',
          num: ''
        }
      ]
    };
  }
};
</script>

