<template>
  <div class="scenes-complete">
    <div class="scenes-complete_warp complete" v-if="complete">
      <div class="iconfont iconl-check"></div>
      <div class="tip-1">提交成功</div>
      <div class="tip-2">
        添加场景完成，请稍等<em>{{ waitTime }}</em
        >秒进入新增布控页面！
      </div>
      <div class="scenes-complete_btn">
        <slot name="buttonGroup">
          <dg-button type="text" @click="go(1)">立即新增布控对象</dg-button>
          <dg-button type="text" @click="go(2)">返回桌面</dg-button>
        </slot>
      </div>
    </div>
    <div class="scenes-complete_warp" v-else>
      <div class="iconfont iconl-close"></div>
      <div class="tip-1">提交失败</div>
      <div class="tip-2">请核对并修改信息后，再重新提交。</div>
    </div>
  </div>
</template>

<script>
export default {
  name: 'scenes-complete',
  props: {
    errors: {
      type: Array,
      default() {
        return [];
      }
    },
    timerFn: Function
  },
  data() {
    return {
      waitTime: 5
    };
  },
  computed: {
    complete() {
      return !this.errors.length;
    }
  },
  methods: {
    init() {
      if (this.complete) {
        this.time = setInterval(() => {
          this.waitTime--;
          if (!this.waitTime) {
            this.go(1);
            this.timerFn && this.timerFn();
          }
        }, 1000);
      }
    },
    go(type) {
      clearInterval(this.time);
      if (type === 1) {
        this.$router.push({ name: 'qwbk' });
      }
      if (type === 2) {
        this.$router.push({ name: 'dashboard' });
      }
    }
  },
  created() {
    this.init();
  },
  beforeDestroy() {
    clearInterval(this.time);
  }
};
</script>

