<template>
  <transition name="el-alert-fade">
    <div
      class="el-alert dg-alert"
      :class="[typeClass, center ? 'is-center' : '', 'is-' + effect, ((title || $slots.title) && (description || $slots.description || $slots.default)) ? 'dg-alert__box' : '']"
      v-show="visible"
      role="alert"
    >
      <i class="el-alert__icon" :class="[ iconClass, isBigIcon ]" v-if="showIcon"></i>
      <div class="el-alert__content">
        <span class="el-alert__title" :class="[ isBoldTitle ]" v-if="title || $slots.title">
          <slot name="title">{{ title }}</slot>
        </span>
        <p class="el-alert__description" v-if="$slots.default && description">
          {{description}}
          <slot></slot>
        </p>
        <p class="el-alert__description" v-if="$slots.default && !description">
          <slot></slot>
        </p>
        <p class="el-alert__description" v-if="description && !$slots.default">{{ description }}</p>
        <i
          class="el-alert__closebtn"
          :class="{ 'is-customed': closeText !== '', 'el-icon-close': closeText === '' }"
          v-show="closable"
          @click="close()"
        >{{closeText}}</i>
      </div>
    </div>
  </transition>
</template>

<script type="text/babel">
import { Alert as ElAlert } from 'element-ui';

export default {
    name: 'DgAlert',

    mixins: [ElAlert]
};
</script>
