<template>
  <button
    class="el-button dg-button"
    @click="handleClick"
    :disabled="buttonDisabled || loading"
    :autofocus="autofocus"
    :type="nativeType"
    :style="ghost && !buttonDisabled ? ghostStyle : ''"
    :class="[
            type ? 'el-button--' + type : '',
            buttonSize ? 'el-button--' + buttonSize : '',
            {
                'is-ghost': ghost && !buttonDisabled,
                'is-disabled': buttonDisabled,
                'is-loading': loading,
                'is-plain': plain,
                'is-round': round,
                'is-circle': circle,
                'is-dashed': dashed
            }
        ]"
  >
    <i
      class="el-icon-loading"
      v-if="loading"
    ></i>
    <i
      :class="icon"
      v-if="icon && !loading"
    ></i>
    <span v-if="$slots.default">
      <slot></slot>
    </span>
  </button>
</template>

<script>
import { Button as ElButton } from 'element-ui';

export default {
    name: 'DgButton',

    mixins: [ElButton],

    props: {
        ghost: Boolean,
        ghostColor: String,
        dashed: Boolean
    },

    computed: {
        ghostStyle() {
            const { disabled, ghostColor } = this;
            if (disabled) {
                return '';
            }
            return ghostColor ? `color:${ghostColor};border-color:${ghostColor};` : '';
        }
    }
};
</script>
