<template>
    <div class="el-card dg-card" :class="cardStyle">
        <div class="el-card__header" v-if="$slots.header || header">
            <slot name="header">
                <span>{{ header }}</span>
            </slot>
            <el-button class="dg-card__more" type="text" v-if="isMore && !$slots.header" @click="handleClick">{{
                moreText
            }}</el-button>
        </div>
        <div class="el-card__body" :style="bodyStyle">
            <slot></slot>
        </div>
    </div>
</template>

<script>
export default {
    name: 'DgCard',
    props: {
        header: {},
        isMore: {
            type: Boolean,
            default: false
        },
        moreText: {
            type: String,
            default: 'More'
        },
        // 是否显示阴影
        shadow: {
            type: String
        },
        // 是否显示边框
        noneBorder: {
            type: Boolean,
            default: false
        },
        // 是否显示底边
        noneBase: {
            type: Boolean,
            default: false
        },
        bodyStyle: {}
    },
    computed: {
        cardStyle() {
            return [
                this.shadow ? 'is-' + this.shadow + '-shadow' : 'is-always-shadow',
                this.noneBorder ? '' : 'is-border',
                this.noneBase ? '' : 'is-base'
            ].join(' ');
        }
    },
    methods: {
        handleClick() {
            this.$emit('click');
        }
    }
};
</script>
