import _ from 'lodash';
import vCharts from 'v-charts';
import mixin from './src/mixin';

let charsObj = {};
let charsArr = [];

Object.keys(vCharts).forEach(item => {
    if (item !== 'install') {
        let currItem = vCharts[item];
        currItem = _.merge(currItem, mixin);
        currItem.install = function(Vue) {
            Vue.component(currItem.name, currItem);
        };
        charsObj[item] = currItem;
        charsArr.push(currItem);
    }
});

export default { charsObj, charsArr };
