import Vue from 'vue';
import Dialog from './src/main';
import dialog from './src/dialog';
import { layerDialog, layer } from './src/layer';
import directive from './src/footer';

Vue.directive('footer', directive);

Dialog.dialog = dialog;
Dialog.layerDialog = layerDialog;
Dialog.layer = layer;
Dialog.directive = directive;

/* istanbul ignore next */
Dialog.install = function(Vue) {
    Vue.directive('footer', directive);
    Vue.component(Dialog.name, Dialog);
};

export default Dialog;
