/**
 * 异步队列
 * @Author: yangjy
 * @Date: 2019-06-19
 * @Project ui-component-v4
 */
export default function(queue, fn, cb) {
    const step = index => {
        if (index >= queue.length) {
            cb();
        } else {
            if (queue[index]) {
                fn(queue[index], () => {
                    step(index + 1);
                });
            } else {
                step(index + 1);
            }
        }
    };
    step(0);
}
