    @Date: 2019-03-07
-->
<template>
  <span :style="{ 'white-space': isWrap ? 'normal' : 'nowrap' }">
    <slot>
      {{ value }}
    </slot>
  </span>
</template>

<script>
export default {
    name: 'DgDefaultTpl',

    props: {
        // 数据值
        value: [String, Number, Boolean],
        scope: Object,
        tableColumnOverflow: String
    },

    computed: {
        // 是否换行:原来方式换行，悬浮换行，提示不换行
        isWrap() {
            let isHover = this.scope.$index == this.scope.store.states.hoverRow;
            let overflow = this.tableColumnOverflow;
            return overflow == 'origin' || (overflow == 'wrap' && isHover);
        }
    }
};
</script>
