import _ from 'lodash';

export default (strats, count, pageTotal = 3) => {
    let pageSize = 10;
    if (strats && _.isFunction(strats)) {
        let height = strats(document.body.clientHeight || window.innerHeight, count);
        if (height <= 0) {
            return [10, 15, 20];
        }
        pageSize = Math.floor(height / 46 - 2);
        pageSize = pageSize || 1;
    }

    // let pageTotal = Math.ceil(count / pageSize);
    let pageSizes = [];
    // 遍历页数表
    for (let i = 1; i <= pageTotal; i++) {
        pageSizes.push(pageSize * i);
    }
    return pageSizes;
};
