<template>
  <transition-group
    tag="ul"
    :class="['el-upload-list', 'el-upload-list--' + listType, { 'is-disabled': disabled }]"
    name="el-list"
  >
    <li
      v-for="(file, indx) in showOne ? files[0] : files"
      :class="['el-upload-list__item', 'is-' + file.status, focusing ? 'focusing' : '']"
      :key="file.uid"
      tabindex="0"
      @keydown.delete="!disabled && $emit('remove', file)"
      @focus="focusing = true"
      @blur="focusing = false"
      @click="focusing = false"
    >
      <img
        class="el-upload-list__item-thumbnail"
        v-if="file.status !== 'uploading' && ['picture-card', 'picture'].indexOf(listType) > -1"
        :src="serverUrl + file[defaultOpt.url]"
        :onerror="error"
        alt
      />

      <!-- 自定义模版 -->
      <template v-if="$scopedSlots.default">
        <a
          class="el-upload-list__item-name"
          @click="handleClick(file)"
        >
          <slot v-bind="file"></slot>
        </a>
      </template>

      <!-- 原始模版 -->
      <template v-else>
        <a
          class="el-upload-list__item-name"
          @click="handleClick(file)"
        >
          <i class="el-icon-paperclip"></i>
          {{ `${file[defaultOpt.name]} (${fileSize(file.size)})` }}
        </a>
      </template>

      <!-- 源码
        <a class="el-upload-list__item-name" @click="handleClick(file)">
          <i :class="el-icon-document"></i>
          {{ file.name }}
        </a>
      -->

      <label class="el-upload-list__item-status-label">
        <i :class="{
            'el-icon-upload-success': true,
            'el-icon-circle-check': listType === 'text',
            'el-icon-check': ['picture-card', 'picture'].indexOf(listType) > -1 }"
        ></i>
      </label>

      <i
        class="el-icon-close"
        v-if="!disabled"
        @click="$emit('remove', file)"
      ></i>

      <i
        class="el-icon-close-tip"
        v-if="!disabled"
      >{{ t("el.upload.deleteTip") }}</i>
      
      <!--
        因为close按钮只在li:focus的时候 display,
        li blur后就不存在了，所以键盘导航时永远无法 focus到 close 按钮上
      -->
      <el-progress
        v-if="file.status === 'uploading'"
        :type="listType === 'picture-card' ? 'circle' : 'line'"
        :stroke-width="listType === 'picture-card' ? 6 : 2"
        :percentage="parsePercentage(file.percentage)"
      ></el-progress>

      <span
        class="el-upload-list__item-actions"
        v-if="listType === 'picture-card'"
      >
        <span
          class="el-upload-list__item-preview"
          v-if="handlePreview && listType === 'picture-card'"
          @click="handlePreview(file, indx)"
        >
          <i class="el-icon-zoom-in"></i>
        </span>
        <span
          v-if="!disabled"
          class="el-upload-list__item-delete"
          @click="$emit('remove', file)"
        >
          <i class="el-icon-delete"></i>
        </span>
        <span
          v-if="!disabled && showUploadIcon"
          class="el-upload-list__item-plus"
          @click="$emit('upload', file)"
        >
          <i class="el-icon-plus"></i>
        </span>
      </span>
    </li>
  </transition-group>
</template>
<script>
import Locale from 'main/mixins/locale';
import { Progress as ElProgress } from 'element-ui';
import { fileSize, write2Class } from './unit.js';

export default {
    name: 'ElUploadList',

    mixins: [Locale],

    data() {
        return {
            focusing: false
        };
    },

    computed: {
      error() {
          const { errorImg } = this;
          return errorImg ? `this.src="${errorImg}"; this.onerror=null;` : '';
      }
    },

    components: { ElProgress },

    props: {
        errorImg: String,
        // 服务器 baseURL
        serverUrl: {
            type: String,
            default: ''
        },
        showUploadIcon: Boolean,
        showOne: Boolean,
        defaultOpt: Object,
        files: {
            type: Array,
            default() {
                return [];
            }
        },
        disabled: {
            type: Boolean,
            default: false
        },
        handlePreview: Function,
        listType: String
    },
    methods: {
        parsePercentage(val) {
            return parseInt(val, 10);
        },
        handleClick(file) {
            this.handlePreview && this.handlePreview(file);
        },
        fileSize,
        write2Class
    }
};
</script>
