import {AbsUserApi} from "./abstract/AbsUserApi";
import {SbootUserApi} from "./sboot/SbootUserApi";
import {DubboUserApi} from "./dubbo/DubboUserApi";

class UserApi extends AbsUserApi{
    api = null;
    constructor(module) {
        super(module);
        //1.初始化api
        if (this.moduletype == "sboot") {
            this.api = new SbootUserApi(module);
        } else if (this.moduletype == "dubbo") {
            this.api = new DubboUserApi(module);
        }
    }

    getCurrentUserMenu(loading) {
        return this.api.getCurrentUserMenu(loading)
    }
}

export {UserApi}