import {AbsQscApi} from "../abstract/AbsQscApi";

class SbootQscApi extends AbsQscApi{
  search(pagingRequest) {
      let data = {};
      data = Object.assign(data, pagingRequest)
      data["search"]=data["queryMap"]
      return this.request({
          url: `/qschema`,
          method: "post",
          data: data,
          baseURL: this.modulePath,
          headers: { "Content-Type": "application/json" }
      });
  }
  getQueryObjcet(id) {
    let p = new Promise(function (resolve, reject) {
      let data={
        "data":{
          "data":{
            id:id,
            fields:[],
            layout:{
              resultTable:{
                thItems:[]
              }
            },
            conditions:[]
          }
        }
      }
      resolve(data);
    });
    return p;
  }
}

export {
  SbootQscApi
}
