/**
 * 消息提示框
 * 提供操作成功、警告、失败、普通提示四种提示框
 * @Author: huangjq
 * @Date:   2019-04-17
 * @Project jz
 */
export default function(alert) {
    let _alert = function(msg, title, btnText, type) {
        alert(msg, title, {
            type: type,
            confirmButtonText: btnText
        });
    };
    let alertFn = function(msg = "提示", title = "提示", btnText, type = "info") {
        _alert(msg, title, btnText, type);
    };
    alertFn.success = function(msg = "操作成功", title = "成功", btnText = "确定") {
        _alert(msg, title, btnText, "success");
    };
    alertFn.info = function(msg = "提示", title = "提示", btnText = "确定") {
        _alert(msg, title, btnText, "info");
    };
    alertFn.warning = function(msg = "警告", title = "警告", btnText = "确定") {
        _alert(msg, title, btnText, "warning");
    };
    alertFn.error = function(msg = "操作出错", title = "错误", btnText = "确定") {
        _alert(msg, title, btnText, "error");
    };
    return alertFn;
}
