/**
 * 消息提示框
 * 提供操作成功、警告、失败、普通提示四种提示框
 * @Author: huangjq
 * @Date:   2019-04-17
 * @Project jz
 */
export default function(confirm) {
    return (msg = "操作成功", title = "提示", setting = {}) => {
        let defaultSetting = {
            confirmButtonText: "确定",
            cancelButtonText: "取消"
        };
        Object.assign(defaultSetting, setting);
        return confirm(msg, title, setting);
    };
}
